module.exports = function(app){
	app.get('/', function(req, res){
        res.render('index', {title:'Mi sitio con NodeJS Genial!!'});
    });
    app.get('/contact', function(req, res){
            var mensaje = [
                    '<h1>hola</h1>',
                    '<p>como estas<b>?</b>',
                    'Gracias</p>'
            ].join('\n');
            res.send(mensaje);
    });
    app.get('/alumno/:userId', function(req, res){
            res.send('Alumno # '+ req.params.userId);
    })
}
